import React from 'react';
import {makeStyles,Typography,Card,CardMedia,CardContent,Button,CardActions} from '@material-ui/core';
import juan from "../images/juan.png";
import TypeWriter from "react-typewriter-effect";
import cv from "../images/cv.txt";

function About({title,dark,id}) {
    const classes= useStyles();
    return (
        <div className={`${classes.section} ${dark && classes.sectiondark}`}>
            <div className={classes.sectioncontent} id={id}>
                <Typography variant="h3">{title}</Typography>
                <Card className={classes.card}>
                    <CardMedia image={juan} className={classes.media} title="pictures" />
                    <CardContent className={classes.cardContent}>
                        <TypeWriter
                        text="Hola soy Yeremi el bandolero"
                        textStyle={{fontSize:"2rem",fontWeight:"700px",color:"tomato"}}
                        startDelay={100}
                        cursorColor="black"
                        typeSpeed={100}
                        />
                        <TypeWriter
                        text="Aunque digan que soy"
                        textStyle={{fontSize:"1.2rem",fontWeight:"500px",color:"black"}}
                        startDelay={2500}
                        cursorColor="black"
                        typeSpeed={100}
                        />
                    </CardContent>
                    <CardActions>
                        <Button variant="contained" className={classes.pdfButton}>
                            <a href={cv}download>
                                Download CV
                            </a>
                            </Button>
                    </CardActions>
                </Card>
            </div>
        </div>
    )
}
const useStyles = makeStyles((theme)=>({

    section:{
        minHeight: "100vh",
      
    },
    sectiondark:{
        background : "#333",
        color:"#fff",
    },
    sectioncontent:{
        maxWidth: "80vw",
        margin: "0 auto",
    },
    media:{
        width:"250px",
        height:"auto",
        borderRadius: "10px",
        objectFit:"cover",
        margin: theme.spacing(5),

    },
    card:{
        height: "70vh",
        display: "flex",
        marginTop: theme.spacing(6),
        position:"relative",
    }
  })) 
export default About
