import Carousel from '@brainhubeu/react-carousel';
import Icon from 'react-fa';
import css from "../images/css.jpg";
import node from "../images/griffin.jpg";
import picReact from "../images/react.png";

// ...
class Contact extends React.Component {
render() {
  return (
    <Carousel
      slidesPerPage={5}
      slidesPerScroll={2}
      infinite
      clickToChange
      centered
      breakpoints={{
        1000: { // these props will be applied when screen width is less than 1000px
          slidesPerPage: 2,
          clickToChange: false,
          centered: false,
          arrows: true,
          infinite: false,
        },
        500: {
          slidesPerPage: 1,
          slidesPerScroll: 1,
          clickToChange: false,
          centered: false,
          arrowLeft: (<Icon className="icon-example" name="arrow-left" />),
          arrowRight: (<Icon className="icon-example" name="arrow-right" />),
          animationSpeed: 2000,
          infinite: false,
        },
      }}
    >
      <img src={css} />
      <img src={node} />
      <img src={picReact} />
    </Carousel>
  );
}
}
